CREATE TABLE ssmdemo1.base_dict
(
    dict_id varchar(32) PRIMARY KEY NOT NULL COMMENT '数据字典id(主键)',
    dict_type_code varchar(10) NOT NULL COMMENT '数据字典类别代码',
    dict_type_name varchar(64) NOT NULL COMMENT '数据字典类别名称',
    dict_item_name varchar(64) NOT NULL COMMENT '数据字典项目名称',
    dict_item_code varchar(10) COMMENT '数据字典项目代码(可为空)',
    dict_sort int(10) COMMENT '排序字段',
    dict_enable char(1) NOT NULL COMMENT '1:使用 0:停用',
    dict_memo varchar(64) COMMENT '备注'
);
CREATE TABLE ssmdemo1.customer
(
    cust_id bigint(32) PRIMARY KEY NOT NULL COMMENT '客户编号(主键)' AUTO_INCREMENT,
    cust_name varchar(32) NOT NULL COMMENT '客户名称(公司名称)',
    cust_user_id bigint(32) COMMENT '负责人id',
    cust_create_id bigint(32) COMMENT '创建人id',
    cust_source varchar(32) COMMENT '客户信息来源',
    cust_industry varchar(32) COMMENT '客户所属行业',
    cust_level varchar(32) COMMENT '客户级别',
    cust_linkman varchar(64) COMMENT '联系人',
    cust_phone varchar(64) COMMENT '固定电话',
    cust_mobile varchar(16) COMMENT '移动电话',
    cust_zipcode varchar(10),
    cust_address varchar(100),
    cust_createtime datetime COMMENT '创建时间'
);
CREATE INDEX FK_cst_customer_user_id ON ssmdemo1.customer (cust_user_id);
CREATE INDEX FK_cst_customer_create_id ON ssmdemo1.customer (cust_create_id);
CREATE INDEX FK_cst_customer_source ON ssmdemo1.customer (cust_source);
CREATE INDEX FK_cst_customer_industry ON ssmdemo1.customer (cust_industry);
CREATE INDEX FK_cst_customer_level ON ssmdemo1.customer (cust_level);
CREATE TABLE ssmdemo1.sys_user
(
    user_id bigint(32) PRIMARY KEY NOT NULL COMMENT '用户id' AUTO_INCREMENT,
    user_code varchar(32) NOT NULL COMMENT '用户账号',
    user_name varchar(64) NOT NULL COMMENT '用户名称',
    user_password varchar(32) NOT NULL COMMENT '用户密码',
    user_state char(1) NOT NULL COMMENT '1:正常,0:暂停'
);
